namespace TH_Bank
{
    public abstract class Menu
    {
        internal int optionCount;
        internal bool access;
        internal string[]? _menu;
        internal int menuWidth;

        protected UserFacade _userFacade;

        public abstract void ShowMenu();

        public abstract void ShowAccounts(User user, AccountDataHandler activeUser);

        internal void Return() // Method to return to Login.
        {
            Console.Clear();
            Console.WriteLine("Taking you to home screen.");
            Thread.Sleep(2000);
            Console.Clear();
            ActiveUserSingleton.Reset();
            Program.LogIn(new UserDataHandler());
            access = false;
            bool login = true;
            return;
        }

        internal void Close() // Close application.
        {
            Console.Clear();
            Console.WriteLine("Thanks for using our bank!");
            Thread.Sleep(2500);
            access = false;
            Environment.Exit(0);
        }
        

        public void LogoText()
        {
            User current = ActiveUserSingleton.GetInstance();
            
            Console.Write("" +
                " _____ _   _  ______  ___  _   _ _   __\r\n" +
                "|_   _| | | | | ___ \\/ _ \\| \\ | | | / /\r\n" +
                "  | | | |_| | | |_/ / /_\\ \\  \\| | |/ / \r\n" +
                "  | | |  _  | | ___ \\  _  | . ` |    \\ \r\n" +
                "  | | | | | | | |_/ / | | | |\\  | |\\  \\\r\n" +
                "  \\_/ \\_| |_/ \\____/\\_| |_|_| \\_|_| \\_/\n");
            Console.WriteLine($"\n##Username:{current.UserName}  ##Access:{current.UserType}.");
        }

        /*------------Methods to create dynamic border based on length on menu options--------*/
        internal int CalculateWidth(int extraWidth)
        {
            int maxWidth = 0;
            foreach (string item in _menu)
            {
                if (item.Length > maxWidth)
                {
                    maxWidth = item.Length;
                }
            }
            return maxWidth + 4 + extraWidth;
        }

        internal void DrawBorder()
        {
            Console.WriteLine(new string('#', menuWidth));
        }

        internal void DrawMenuItem(string item)
        {
            Console.WriteLine($"# {item.PadRight(menuWidth - 4)} #");
        }
